mapper.select:
SELECT COUNT(*)
FROM TBL_MEMBER
WHERE MEMBER_EMAIL = #{email}

mapper.update:
UPDATE TBL_MEMBER
SET MEMBER_KAKAO_ID          = #{kakaoId},
    MEMBER_KAKAO_PROFILE_URL = #{profileUrl, jdbcType=VARCHAR}
WHERE ID = #{id}

UPDATE TBL_MEMBER
SET MEMBER_PROFILE_ID = #{memberProfileId}
WHERE ID = #{memberId}

UPDATE TBL_MEMBER
SET MEMBER_KAKAO_PROFILE_URL = #{memberKakaoProfileUrl}
WHERE MEMBER_EMAIL = #{memberEmail}

UPDATE TBL_MEMBER
SET MEMBER_NICKNAME     = #{memberNickname},
    MEMBER_INTRODUCTION = #{memberIntroduction}
WHERE ID = #{id}
